<?    function msgWrite($msg) {        $fp = fopen("log.txt", "a");        fwrite($fp, $msg . "\n");        fclose($fp);    }?><!DOCTYPE html><html>    <head>        <meta charset="utf-8">        <title>chat</title>    </head>    <body>        <?php            if(array_key_exists('talk', $_REQUEST)) {                $talk = $_REQUEST['talk'];                msgWrite($talk);            }            $fp = fopen("log.txt", "r");            while($line = fgets($fp)) {                echo "$line<br />";            }            fclose($fp);        ?>        <form>            <input type="text" name="talk">            <input type="submit" value="送信">        </form>    </body></html>